package java0414;

public class Main1 {

	// #1 사칙연산, 연산 후 대입

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int k33_ii; // k33_ii라는 정수형 변수를 선언한다.

		k33_ii = 1 + 2; // 1 + 2 연산을 수행 후 변수 k33_ii에 대입한다.
		System.out.printf("#1-1 : %d\n", k33_ii);	// #1-1 : k33_ii값 이 출력되도록 입력하고, 마지막에 \n(줄내림)을 입력한다.

		k33_ii = 1 + 2 * 3; // 1 + 2 * 3 연산을 수행 후 변수 k33_ii에 대입한다.
		System.out.printf("#1-2 : %d\n", k33_ii);	// #1-2 : k33_ii값 이 출력되도록 입력하고, 마지막에 \n(줄내림)을 입력한다.
	}
}
